jQuery(document).ready(function($){jQuery('form#sppro-login').on('submit',function(e){e.preventDefault();thisForm=jQuery(this);statusArea=thisForm.find('.sppro-status');statusArea.show().html(ajax_login_object.loadingmessage);sppro_animate_me(statusArea,'slideInUp');username=thisForm.find('.username').val();pass=thisForm.find('.password').val();login_security=jQuery('#login_security').val();if(username==''){statusArea.html('<p class="error">'+ajax_login_object.emptyusername+'</p>');sppro_animate_me(statusArea,'slideInUp');return!1}
if(pass==''){statusArea.html('<p class="error">'+ajax_login_object.emptypassword+'</p>');sppro_animate_me(statusArea,'slideInUp');return!1}
jQuery.post(ajax_login_object.ajaxurl,{'action':'sppro_login_function','username':username,'password':pass,'login_security':login_security,'_wp_http_referer':jQuery('input[name="_wp_http_referer"]').val(),},function(response){response=jQuery.parseJSON(response);statusArea.html(response.message);if(response.loggedin==!0){setTimeout(function(){location.reload()},2000)}else{sppro_animate_me(statusArea,'slideInUp')}})});jQuery("form#sppro-lostpassword").submit(function(){thisForm=jQuery(this);statusArea=thisForm.find('.sppro-status');statusArea.show().html(ajax_login_object.loadingmessage);user_name=thisForm.find('.user_name').val();lostpassword_security=thisForm.find('#lostpassword_security').val();if(user_name==''){statusArea.html('<p class="error">'+ajax_login_object.emptyusername+'</p>');sppro_animate_me(statusArea,'slideInUp');return!1}
jQuery.post(ajax_login_object.ajaxurl,{'action':'lost_pass_callback','user_name':user_name,'lostpassword_security':lostpassword_security,'_wp_http_referer':thisForm.find('input[name="_wp_http_referer"]').val(),},function(response){if(response.success==!0){statusArea.removeClass('success').addClass('error')}else{statusArea.removeClass('error').addClass('success')}
statusArea.html(response.data.message);sppro_animate_me(statusArea,'slideInUp')});return!1})});function sppro_animate_me(ele,effect){if(ele===undefined)return!1;if(effect===undefined)effect='shake';ele.addClass(effect+' animated');setTimeout(function(){ele.removeClass(effect+' animated')},10000)}